float light = 0.7;
float outRadius = 0.65;
float inRadius = 0.4;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(texture(iChannel0,uv).xyz,1.0);
    vec3 textureColor = vec3(texture(iChannel0,uv).xyz);

   	vec2 pos = fragCoord.xy / iResolution.xy - 0.5;
	float posLength = length(pos);
   	float vignetteColor = smoothstep(outRadius, inRadius, posLength);


    vec3 finalColor = mix(textureColor, textureColor*vignetteColor, light);
    fragColor = vec4(finalColor,1.0);
}
