void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    vec2 p = fragCoord.xy / iResolution.xy;

    vec3 colA = texture( iChannel0, p ).xyz;
    vec3 colLast = texture( iChannel1, p ).xyz;

    fragColor = vec4(colA-colLast, 1.0 );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float frameTime = 0.050;
    float doubleFrameTime = 2.0*frameTime;
    vec2 p = fragCoord.xy / iResolution.xy;
    vec3 colOld = texture( iChannel0, p ).xyz;
    vec3 colNew = texture( iChannel2, p ).xyz;

    if(iTime - doubleFrameTime*floor(iTime/(doubleFrameTime)) < frameTime)
    {
    	fragColor = vec4(colNew,1.0);
    }
    else
    {
        fragColor = vec4(colOld,1.0);
    }
}

