//uniform vec2 iResolution;
//uniform vec2 iMouse;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 f = fragCoord.xy/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;
    float radius = .3;


	vec4 baseColor = texture(iChannel0, f);
    vec4 color = baseColor;

    // TODO make this round
    float MtoF = 
        min(sqrt(pow(m.x-f.x, 2.) + pow(m.y-f.y, 2.))/radius, 1.);


    //if(f.x>(m.x-radius) && f.x<m.x+radius)
    //	if(f.y>(m.y-radius) && f.y<m.y+radius)
    //   	color.g = .0;


   	color.r = (1.-MtoF) * color.r;
   	color.g = (1.-MtoF) * color.g;
   	color.b = (1.-MtoF) * color.b;

	fragColor = color;
}
