mat2 rotate(float a) {
    return mat2(-sin(a), cos(a),
                 cos(a), sin(a));
}

void mainImage( out vec4 o, in vec2 p ) {
    p /= iResolution.xy;
    p -= 0.5;
    p.x *= iResolution.x / iResolution.y;

    vec2 p2 = p;

    vec2 q = p;
    vec2 u = p;

    float size = 0.35;
    float deg30 = 0.523599;


    float a = 0.15 + iTime * 0.1;

    mat2 rotation =  mat2(-sin(a), cos(a),
                           cos(a), sin(a));

    p *= rotation;
    p2 *= rotation;


    u.x += sin(iTime) * 0.1;
    u.y += cos(iTime) * 0.1;


    float deform = (0.5 + 2.5 * dot(u, u));
    p *= deform;
    p2 *= deform;

    p.x = mod(p.x, 0.05 * size * 2.0);
    p.y = mod(p.y, 0.05 * size);
    p -= 0.025 * size;



    p2.y -= 0.025 * size;
    p2.x = mod(p2.x + 0.05 * size, 0.05 * size * 2.0);
    p2.y = mod(p2.y, 0.05 * size);
    p2 -= 0.025 * size;


    float r = length(p) - 0.0075 * size;


    r = min(r, abs(p.x) - 0.00001 * size);  


    float cut = p.x;

    p *= rotate(deg30);
    r = min(r, max(-cut, abs(p.x) - 0.00001 * size));

    p *= rotate(-deg30);
    r = min(r, max(-cut, abs(p.x) - 0.00001 * size));



    r = min(r, length(p2) - 0.0075 * size);

    r = min(r, abs(p2.x) - 0.00001 * size);  

    cut = p2.x;

    p2 *= rotate(deg30);
    r = min(r, max(-cut, abs(p2.x) - 0.00001 * size));
    p2 *= rotate(-deg30);
    r = min(r, max(-cut, abs(p2.x) - 0.00001 * size));


    float sphereMask = length(q) - 0.35;
    r = max(r, sphereMask);

    float foreground = smoothstep(0.0025 * size, 0.0, r);
    float background = 1.0 - foreground;

    o.rgb = (
        foreground * vec3(1.0, 1.0, 1.0) + 
        background * vec3(0.0, 0.0, 0.0) 
        + smoothstep(0.0, 0.001, sphereMask) - sphereMask * 0.15
    );
}
