float PI=3.14159;

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float pix(vec2 uv){
	return step(0.5,rand(floor(uv)));	
}

float hm(vec2 uv){

	vec2 nuv = (fract(uv)-0.5)*2.;
	vec2 pos = sign(nuv)*0.9;

	float d = length(nuv);

	float v = pix(uv);

	if(d>1.){

	vec2 p1=vec2(pos.x,0.),
		 p2=vec2(0.,pos.y);

	float v1 = pix(uv+p1),
		  v2 = pix(uv+p2);

		if(v1==v2){
			if(v==pix(uv+pos) && v!=v1) v=0.;
			else v=v1;
		}
	}	

	return mix(pix(uv),v,smoothstep(d,1.,1.01));
}

vec3 shade(vec2 uv){
	uv*=5.;
	return vec3(hm(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

	vec3 color = shade(uv);
	fragColor = vec4(color,1.0);
}
