#define PI 3.14159265359
bool keyToggle(int ascii) 
{ return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.); }


void mainImage( out vec4 O, vec2 fragCoord )
{
	vec2 R = iResolution.xy,
        uv = (2.* fragCoord -R) / R.y,
	    mouse = iMouse.xy / R;
	float n = mouse.x;
	if (iMouse.z<=0.) n = .5; // sin(.01*iTime);

	float r = length(uv), a = atan(uv.y,uv.x); // to polar

	float k = 1.* iResolution.y/1.5,
	      s = a -  k * r,   l,t;  
#if 0 // no bug in this version
		uv = 1./1.5*(fragCoord-R/2.);
		r = length(uv)/4.; a = atan(uv.y,uv.x); 
		s = r - a/(2.*PI);
	    t = floor(s)+a/(2.*PI); // turns
	    l = PI*t*t+ 10.*iTime;// int (a/2PI da)
		s *= 2.*PI;
#else	
		s = a -  k * r;
		t = floor(s/(2.*PI)) * (2.*PI); 
		l = .1*(a+k*t)*t + 10.*iTime;
#endif
	float c = smoothstep(-1.,1.,sin(s))*smoothstep(-1.,1.,sin(10.*n*l));

	O = keyToggle(67)
	 	  ? vec4(sin(c))
	      : sin(c*vec4(1,1.2,1.4,0));
}
