void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	ivec2 uv = ivec2(fragCoord.xy);// * int(iTime*60.) * 10000; // - iResolution.xy*0.5 -0.5
    //uv =  abs(uv);
    int prod = uv.x*uv.y * int(1. + 1000.*min(pow(iTime*0.1,6.2),10.)) * (1 + iFrame*iFrame/512);// * floatBitsToInt(iTime*0.1);
    int ired = prod;
    int igrn = prod/256;
    int iblu = prod/256/256;
	fragColor = vec4(vec3(fract(float(ired)/256.0),fract(float(igrn)/256.0),fract(float(iblu)/256.0)),1.0);
}
