void mainImage1( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(1.0, 0.4, 0.1);
	fragColor = vec4(col, 1.0);
