void mainImage11( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(1.0, 0.4, 0.1);

    col *= p.x;

	fragColor = vec4(col, 1.0);
}

void circGradient( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(1.0, 0.4, 0.1);

    col *= length(p - vec2(0.5, 0.5));

	fragColor = vec4(col, 1.0);
}

void smoothstepExample( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5, 0.5);

    vec3 col = vec3(1.0, 0.4, 0.1);

    col *= smoothstep(0.2, 0.3, length(q));

	fragColor = vec4(col, 1.0);
}

void shapeExample( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5, 0.5);

    vec3 col = vec3(1.0, 0.4, 0.1);

    float r = 0.2 + 0.1*cos( atan(q.y, q.x ) * 10.0 );
    col *= smoothstep(r, r+0.01, length(q));

	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5, 0.5);

    vec3 col = vec3(1.0, 0.4, 0.1);

    float r = 0.2 + 0.1*cos( atan(q.y, q.x ) * 10.0 + 20.0*q.x + 9.0 );
    col *= smoothstep(r, r+0.01, length(q));


    r = 0.01;
    col *= 1.0 - (1.0 - smoothstep(r, r+0.001, abs(q.x)))*(1.0 - smoothstep(0.0, 0.1, q.y));

	fragColor = vec4(col, 1.0);
}
