void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    const float blurFactor = 16.0;
    const float mouseSize = 32.0;

    vec3 colour;

    if(distance(iMouse.xy, fragCoord.xy) < mouseSize) {
        for (float i = -blurFactor / 2.0; i < blurFactor / 2.0; i++) {
            for (float j = -blurFactor / 2.0; j < blurFactor / 2.0; j++) {
                vec2 newFragCoord = fragCoord.xy + vec2(i, j);
                vec2 newUV = newFragCoord.xy / iResolution.xy;
                vec3 tex = texture(iChannel0, newUV).rgb;
                colour.rgb += tex.rgb;
            }
        }
        colour.rgb /= blurFactor * blurFactor;
    } else {
     	vec2 uv = fragCoord.xy / iResolution.xy;
        vec3 tex = texture(iChannel0, uv).rgb;
        colour = tex;
    }

    fragColor = vec4(colour, 1.0);
}
