// triangular wall-paper 2015 JT
// as usual Fabrice made it smaller :-D

void mainImage( out vec4 o,  vec2 I ) // FabriceNeyret2's compactified 143chars version
{
    I = abs( 2. * fract( I/iResolution.xy * mat2(2,2,-2,2) ) - 1.);
    o = texture(iChannel0, .5 < fract((I.x+I.y)/2.) ? 1.-I : I.yx);
}

