#define R iResolution
#define T iTime
#define EPS .02

float sq = sqrt(26.);

float f(vec2 v, vec2 p)
{
    return dot(p * p, v);
}

float absmax(float v)
{
    return max(0., 2. - v);
}

float isInSuperEllipse(vec2 p, vec2 o, float r, float n)
{
    return pow(abs(2. * (p.x - o.x) / r), n) + pow(abs((p.y - o.y) / r), n);
}

float eyes(vec2 p)
{
    if (p.y > 1. && p.y < 2. && abs(p.x) < .4)
    {
        float coef = length(texture(iChannel1, vec2(1), 1.).xy) * .45;
        if (coef < .22)
            return 0.;
        coef *= 1.2;
        float e = isInSuperEllipse(p, vec2(.25, 1.5), coef, coef);
        if (e < 1.)
        	return 1. - e;
        e = isInSuperEllipse(p, vec2(-.25, 1.5), coef, coef);
        if (e < 1.)
        	return 1. - e;
    }
    return 0.;
}


float batman(in vec2 p)
{
    vec2 ap = abs(p);
    float l = f(vec2(1. / 36., 1. / 9.), p) - 1.;
    if (l > 0.)
        return 1.;
    l += absmax(f(vec2(2.3, .3), p + vec2(3.05, 2.3)));
    l += absmax(f(vec2(2.3, .3), p + vec2(-3.05, 2.3)));
    l += absmax(f(vec2(1.9, 1. / 2.7), vec2((5. * (p.x + 1.) + (p.y + 3.)) / sq, (-(ap.x + 1.) + 5. * (p.y + 3.)) / sq)));
    l += absmax(f(vec2(1.9, 1. / 2.7), vec2((5. * (p.x - 1.) - (p.y + 3.)) / sq, (-ap.x - 1. + 5. * (p.y + 3.)) / sq)));
    l += absmax(pow(1. * (ap.x - 1.71), 4.) - (p.y - 1.8));
    l += absmax(pow(1. * (1.71 - ap.x), 4.) - (p.y - 1.8));
    l += absmax(pow(2.5 * ap.x, 8.) - (p.y - 3.5));
	return l;
}

float batman2(in vec2 p)
{
    vec2 ap = abs(p);
    float l = f(vec2(1. / 36., 1. / 9.), p) - 1.;
    if (l > 0.)
        return 1.;
    l += absmax(f(vec2(2.3, .8), p + vec2(3, 2.3)));
    l += absmax(f(vec2(2.3, .8), p + vec2(-3, 2.3)));
    l += absmax(f(vec2(1.9, 1. / 2.7), vec2((5. * (p.x + .95) + (p.y + 3.5)) / sq, (-(ap.x + .95) + 5. * (p.y + 3.5)) / sq)));
    l += absmax(f(vec2(1.9, 1. / 2.7), vec2((5. * (p.x - .95) - (p.y + 3.5)) / sq, (-ap.x - .95 + 5. * (p.y + 3.5)) / sq)));
    l += absmax(pow(1. * (ap.x - 1.71), 4.) - (p.y - 1.8));
    l += absmax(pow(1. * (1.71 - ap.x), 4.) - (p.y - 1.8));
    l += absmax(pow(2.3 * ap.x, 8.) - (p.y - 3.5));
	return l;
}

void mainImage(out vec4 C, in vec2 U)
{
	vec2 uv = 2. * U.xy / R.xy - 1.;
    uv.x *= R.x / R.y;
    uv *= 5.;
	C = vec4(vec3(0), 1);
    vec2 tex1 = uv + texture(iChannel0, uv, .5).x - .5;
    float font = f(vec2(1. / 32., 1. / 9.), tex1);
	float deg = smoothstep(0., 1.5, font);
    if (font < 1.3)
        C.rgb = vec3(.83 * deg, .7 * deg, .1 * deg);
    float bat = batman(uv);
    if (batman(uv - vec2(0,.1)) < EPS + .04 && uv.y > 0.)
        C.rgb = vec3(.33,.33,0.33);
    if (batman2(uv - vec2(0,.1)) < EPS + .04 && uv.y < 0.)
        C.rgb = vec3(.66 * smoothstep(0., 2., length(uv) / 2.));
    if (bat < EPS)
    	C.rgb = vec3(eyes(uv));
}
