float rx = iResolution.x/iResolution.y;

vec3 color(vec3 col, vec2 center, float radius, vec2 uv){
    float d = distance(vec2(center.x*rx,center.y),uv);
    float v = smoothstep( 0., radius, d );
    return col * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //base
	vec2 uv = fragCoord.xy/iResolution.yy;
	vec3 col = vec3(.5);
    vec3 col2 = vec3(0.5);

    //push somecolor on it
    col2 += .9*color(vec3(.3,abs(sin(iTime))*.4,.5),vec2(.5,.6),.6, uv);
    col2 += .9*color(vec3(abs(cos(iTime/3.))*.5,.2,.2), vec2(sin(iTime/2.)*.3+.5,cos(iTime)*.5+.6), 1., uv);
    col2 += 1.8*color(vec3(.45,abs(cos(iTime/3.))*.05+.4,.15), vec2(.7,.5), 1.2, uv);
    col2 += .6*color(vec3(.2,.2,.15), vec2(cos(iTime/2.),.7), 1.2, uv);

    //basic
    col += col2*.5;
    col *= col/2.;
	fragColor = vec4(col,1.0);    
}
