void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    if (iMouse.z > 0.) 
    	fragColor = texture(iChannel0, uv*.25) - .2;
    else 
        fragColor = texture(iChannel1, uv*.25);

}

//  iChannel0 has "linear" sampler filter, which is ignored
//  iChannel1 has "nearest" sampler filter

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord.xy/iResolution.xy);
}
