//cloned from https://www.shadertoy.com/view/lscSWn#

vec4 lin2srgb( vec4 c ) { return pow(c,vec4(1.0/2.4)); }
vec4 srgb2lin( vec4 c ) { return pow(c,vec4(    2.4)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
  	vec2 uv = fragCoord.xy / iResolution.y;

    float r = distance(uv, vec2(0.5*aspect, 0.5));
    float radial = (r-0.4) / 0.75;
    radial = clamp( radial, 0.0, 1.0 ); //note: change clamp to control how far down reduction goes

    float pat = (fragCoord.x / iResolution.x < 0.5)
              ? texture( iChannel1, fragCoord/iChannelResolution[1].xy, -10.0 ).x
              : texture( iChannel2, fragCoord/iChannelResolution[2].xy, -10.0 ).x;

    bool checker = pat < radial;
    bool outside = r > 0.4;

    if( checker )
    {
		fragColor = vec4(0);
        return;
    }

    vec4 color = srgb2lin( texture(iChannel0, fragCoord.xy / iResolution.xy) );

    //note: compensate for loss of luminance due to black pixels
    float steps = floor(radial*64.0) / 64.0;
    color *= 1.0 + steps;

    fragColor = lin2srgb(color);
}
