float smax( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float la = abs(uv.x-.5);
    la+=texture(iChannel0, (uv+la*iTime)/la).r/30.;
    float lb = abs(uv.y-.5);
    lb+=texture(iChannel0, (uv+lb*iTime)/lb).r/30.;
    float l = la+lb-smax(la, lb,.1);
	fragColor = texture(iChannel0, (uv+l*iTime)/l)*l*2.;
}
