void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define brushSize 40.0

#define survive n > 1 && n < 4
#define birth n == 3

#define U(p) ((fragCoord+p)/iResolution.xy)
#define P(p) texture(iChannel0, U(p))
#define C(v) (int(ceil(P(v).x)))
#define N(r) for(float i=.0;i<8.;i++) { n+=C(vec2(floor(cos(i*r)+.5),floor(sin(i*r)+.5))); }
#define z2 vec2(.0)
#define z4 vec4(.0)
#define o4 vec4(1.)

//noise see https://www.shadertoy.com/view/ltB3zD
float snoise(in vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float d = .002;
    vec4 c = P(z2) - vec4(d * 1.2, d * .5, d, .0);

    int n = 0;
    N(.7853981633);
    if(iTime > 0.1)
    {
        if(distance(iMouse.xy, fragCoord) < brushSize && iMouse.z > .0)
            fragColor = o4;
        else
            fragColor = C(z2) == 0 ? (birth ? o4 : z4) : (survive ? c : z4);
    }
    else
    {
        fragColor = vec4(snoise(fragCoord) > 0.8 ? 1.0 : 0.0);
    }



}
