float speed = 70.;
float dist = 60.; 
float zoom = 0.0004;

vec3 color1 = vec3(0.168, 0.811, 0.941);
vec3 color2 = vec3(0.168, 0.933, 0.424);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    // a intensity from noise
    float a = max(0., 1. - distance(fragCoord, iResolution.xy/2.)/dist);


    vec2 textureCoord = vec2(fragCoord.x, fragCoord.y - iTime*speed); 
    vec4 text = texture(iChannel0, textureCoord * zoom );


    float i = text.x;

    a *= a;

    a /= i/10.; 


    vec3 color = color1 * a;  

    // Output to screen
    fragColor = vec4(color, 1.);
}
