float rand ( vec2 uv) {return  fract(sin(dot(floor(uv),vec2(75.365,12.365)))*4285.365);}
float noise ( vec2 uv) {
float a = rand(uv+iTime);
float b = rand (uv+vec2(1,0)-iTime);
 float c  = rand (uv+vec2(0,1)+iTime);
 float d  = rand ( uv+vec2(1,1)-iTime);
vec2 u = smoothstep (0.0,1.0,fract(uv));
return mix (a,b,u.x)+(c-a)*u.y*(1.0-u.x)+(d-b)*u.x*u.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv =-1.+2.* fragCoord/iResolution.xy;
  float noi = noise(vec2(noise(uv*15.),noise(uv*10.))*25.);
         float random = (fract((sin(dot(vec2(75.2654,12.3625),floor(uv*200.))))*4593.39+iTime*10.)); 
    float random2 = fract(sin(iTime)*45886.3265+iTime);
    vec2 uv2 = uv*distance(vec2(0),uv*random2*2.)+noi;

    float bl1 = 0.05;
    float h1 = -0.8;
    float int01 = 0.1;
    float nbr1 =1.;
    float li01 = 0.;
    float resu = 0.;

    for( int i =0 ;i<40;i++){
        float j = float (i);
      h1 = -0.8*(0.025*j);
      nbr1 = mix(12.,25.,iTime*5.);
      nbr1 +=(40.-j)*0.5;
      int01 = 0.1;
      int01=j*0.01;
     li01 = smoothstep(h1+bl1,h1-bl1,uv2.g+=sin(uv.r*nbr1+(sin(uv2.g*20.+iTime)*(1.))+(sin(uv2.g*20.-iTime)*0.1)+sin(iTime)+random2*10.)*int01)*0.025;
     resu +=li01;

   }
   float resu3 = mix(1.-resu,resu,random2);

   float pt01 = mod(smoothstep(0.5,mix(0.1,0.9,sin(iTime*10.)),distance (vec2(0.),uv*random2*vec2(2.,1.))),sin(iTime)+random);
   float resu2 = mix(1.-resu3,resu3,pt01);

    fragColor = vec4(resu2,resu2,resu2,1.0);
}

