 float r3 = (it(t1*150.)-0.5)*2.*pow(no((t3+45.+floor(t.w*0.1)))*0.7,(t1*3.)*2.+0.01);
 return vec2(r3+r2+r1)*0.5;
}
vec2 s2 (vec4 t){
