// variant of https://shadertoy.com/view/lscfDr

#define C      max(max(a.x,a.y),a.z)
#define B(x,r) t = min(t, ( a = abs(q), a.x/=r, C ) ) // or just max.yz
                                              // NB: /r is bad. but cause a cool glow :-)
void mainImage(out vec4 O, vec2 U) {

    float t = iTime,v, g=0.;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 30./q, a;                       // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        a = abs(q), // v = C - 5.,            // bounding cube
        a = abs(a-3.), t = C,                 // 8 small cubes
        B(x,5.), B(y,5.), B(z,5.)-1.,         // 3 boxes
      //t = max(v,t),
        v = C-5., g += exp(-v*v),             // glow
        p += t*D;                             // step forward = dist to obj

  //O.r += .02*g;                             // glow
  //v = C-5.; O.r += exp(-v*v);

}
