vec2 complex_mul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;

    vec2 center = vec2(-0.16, 1.0405);

    float time = iTime * 3.;

    float zoom = .25 * exp(time / 5.);

    vec2 c = (uv - vec2(.5, .5)) / zoom + center ;

    vec2 z = vec2(0, 0);

    float iterations = time + 50.;

    bool is_out = false;
    float i = 0.;
    for(i=0.;i<iterations && !is_out;i++) {
        z = complex_mul(z, z) + c;
    	is_out = length(z) > 2.;
    }

    vec3 color = vec3(0,0,0);

    if(is_out)
        color = vec3(1, 1, 1) * i / float(iterations);

    fragColor = vec4(color,1.0);
}
