float c_textureSize = 256.0;

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 percent = (fragCoord.xy / iResolution.xy);   
    float time = floor(iTime);

    // choose a random pixel
    vec2 pixel = vec2(
        rand(vec2(time*7.23, time*3.14)),
        rand(vec2(time*2.19, time*28.28))
	);
    pixel = (floor(pixel*c_textureSize) + 0.5) / c_textureSize;

    // choose a random line segment in a pixel -> the offset is in (0,0) to (1,1)
    vec2 start = vec2(
        rand(vec2(time*0.6449070363+0.9338558165, time*0.7130781021+0.8169828401)),
        rand(vec2(time*0.0381505535+0.6330304460, time*0.0660240592+0.3761387183))
    );
    vec2 stop = vec2(
        rand(vec2(time*0.0452455944+0.2342629428, time*0.2814552243+0.0775322882)),
        rand(vec2(time*0.2106506928+0.4904241435, time*0.6447241528+0.6153393765))
    );
    start /= c_textureSize;
    stop /= c_textureSize;

    // sample along the path.
    vec2 offset = vec2(3.0f * percent.x*percent.x, percent.x*percent.x*percent.x*percent.x);
    offset /= c_textureSize;
    vec2 uv = pixel + offset;

    // draw the curves
    vec3 curveValues = texture(iChannel0, uv).xyz;
    float edge = 1.0 / iResolution.y;
    fragColor = vec4(smoothstep(percent.y-edge, percent.y+edge, curveValues), 1.0);
}
