const float   NUM_LINES 	= 100.0;
const float   INV_NUM_LINES = 1.0 / NUM_LINES;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float nL = floor(uv.y * NUM_LINES);
    float x = mod(nL, 2.0);
    float posInTrans = (nL * INV_NUM_LINES)*0.5 + x * 0.5;


    float trans = (sin(iTime)+1.0)*0.5;
    trans = step(posInTrans, trans);

    vec3 src = texture(iChannel0, uv).rgb;
    vec3 dest = texture(iChannel1, uv).rgb;

	fragColor.rgb =  mix(src, dest, trans);
}
