void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord.x -= iResolution.x/2.0 - iTime*1000.0;
    fragCoord.y -= iResolution.y/2.0;
   	fragCoord.x /= iResolution.y/10.0;
   	fragCoord.y /= iResolution.y/10.0;
    if ( abs(sin(fragCoord.x)-fragCoord.y) < 0.04 ) fragColor = vec4(1.0,1.0,1.0,1.0);
    else fragColor = vec4(0.0,0.0,0.0,0.0);
}
