#define TWOPI (3.14159 * 2.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += sin(iTime*0.5 +  uv.y*10.) * 0.05;
    uv.y += cos(iTime*0.5 +  uv.x*10.) * 0.05;

    // Time varying pixel color
    vec3 col = texture(iChannel1, uv).rgb;

    col.b = col.b - iMouse.x *0.001 ;
    col.r = col.r - col.r;
    col.g = col.g - col.g;

    col = sin(col + length(col)*30.0 + iMouse.y * 0.001) * 0.5 + 0.5;

    col = col - 0.70;

    // Output to screen
    fragColor = vec4(col,1.0);
}
