void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float c_TileSize = 32.0;    // tile size
    const float c_LightRadius = 5.0;  // light radius (in tiles)

    const float c_LightPower = 1.0;  

    vec2 discrete;
    discrete.x = length(floor((gl_FragCoord.x + c_TileSize/2.0)/c_TileSize)*c_TileSize - iMouse.x) / c_TileSize;
    discrete.y = length(floor((gl_FragCoord.y + c_TileSize/2.0)/c_TileSize)*c_TileSize - iMouse.y) / c_TileSize;

    float light_intensity = 1.0 - pow(length(discrete) / c_LightRadius, c_LightPower);

    fragColor = texture(iChannel0, gl_FragCoord.xy / c_TileSize / 1.5); 
    fragColor.rgb += vec3(light_intensity, light_intensity * 0.5, light_intensity*0.2);
}
