//#define SHOW_BUF_A

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv  = fragCoord.xy / iResolution.xy;
    vec2 pos = texture(iChannel0, uv).xy;

    fragColor = texture(iChannel1, uv);

    if(uv.x < pos.x + .05 && uv.y < pos.y + .05 && uv.x > pos.x - .05 && uv.y > pos.y - .05) {

        fragColor = vec4(1.);

    }

    #ifdef SHOW_BUF_A
    if(uv.x < .1 && uv.y < .1) {

        fragColor = texture(iChannel0, uv / .1);

    }
    #endif
}

#define SPEED 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tex = texture(iChannel0, fragCoord / iResolution.xy);
    if(tex.xy == vec2(0.)) tex = vec4(.5,.5,.2,.1);

    vec2 pos = tex.xy;
    vec2 vel = tex.zw;

    if(pos.x <= 0.) vel = reflect(vel, vec2(1.,0. ));
    if(pos.y <= 0.) vel = reflect(vel, vec2(0.,1. ));
    if(pos.x >= 1.) vel = reflect(vel, vec2(-1.,0.));
    if(pos.y >= 1.) vel = reflect(vel, vec2(0.,-1.));

    pos -= vel * SPEED * iTimeDelta;

   	if(iMouse.z > 0.) {

        vel = vec2(.5) + ((iMouse.xy / iResolution.xy) * 2. - 1.);
        pos = vec2(.5) + (vel * .05);

        vel = normalize(vel);

    }

    fragColor = vec4(pos,vel);
}
