float rand(in vec2 uv) {
    return fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

mat2 rotate(float a) {

    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float getPos(vec2 p, float t) {

    p *= rotate(length(p * .1) - iTime * .1);

    return length(p * t) * cos(8. * atan(p.x, p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = mod(iTime * .0125, 1.);

    vec2 p = -1. + 2. * uv;
    p.x *= iResolution.x / iResolution.y;

    p *= 3.;

    float f = getPos(p, t) * length(p * t) + rand(p) * .1;

    vec3 c = vec3(1.0) * (1. - f) / length(p);

    c *= (c + .5);


    c += vec3(1. * f , .5 * f, rand(p + iTime) * f);

	fragColor = vec4(c, 1.0);
}
