void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float wa = .25;
    float wb= 0.25;
    float ha = 0.75;
    float hb = 0.75;
    float s = uv.x;
    float t = uv.y;

    float amount = (smoothstep(wa, wb, s) - smoothstep(ha, hb, s)) * (smoothstep(wa, wb, t) - smoothstep(ha, hb, t));
    vec3 r = mix( vec3(1.,1.,1.), vec3(0.,0.,0.), amount);

    fragColor = vec4(r, 1);
}
