void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

// Convert r, g, b to normalized vec3
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

bool keyPress(int ascii) {
	return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}

vec4 red(){return vec4(rgb(225.0, 95.0, 60.0), 1.); }

float circle(vec2 uv, vec2 pos, float r, float softness) {
    float d = r - length(pos - uv); 
    d = d/(r*clamp(softness, .001, 1.));
	float t = clamp(d, 0., 1.);
	return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){ 
    if (keyPress(32)) { fragColor = red(); return; } 

    vec2 uv = fragCoord.xy / iResolution.xy;

    float cr = circle(fragCoord.xy, iMouse.xy, 50.0, 0.92);    
    vec4 res = vec4(rgb(225.0, 95.0, 60.0)*cr, cr);

    vec4 bg =texture(iChannel0, uv);
    if(iMouse.z >0.){// Blend the two
        fragColor =  vec4(bg.rgb + res.r*res.a, 1.0);
    }else{
        fragColor = bg;
    }
}
