void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      if(iTime <= 0.1|| iFrame < 2){
        vec2 coord = fragCoord.xy/iChannelResolution[0].xy*iChannelResolution[1].xy;
        float grey = dot(texelFetch(iChannel1, ivec2(coord), 0), vec4(.3, .59, .11, 0.0));
        fragColor = vec4(grey, grey, grey, 1.0);
        return;
    }
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0).rrra;
}

const float C_INV = 1.0/4.0;

vec4 texelFetchWrap(vec2 coord){
    coord.x = mod(coord.x, iChannelResolution[0].x);
    coord.y = mod(coord.y, iChannelResolution[0].y);
    return texelFetch(iChannel0, ivec2(coord.xy), 0);
}

float calc_ddu(vec2 coord){
    float sum = 0.0;
    sum += texelFetchWrap(coord.xy + vec2(1.0,0.0)).r;
    sum += texelFetchWrap(coord.xy + vec2(-1.0,0.0)).r;
    sum += texelFetchWrap(coord.xy + vec2(0.0,1.0)).r;
    sum += texelFetchWrap(coord.xy + vec2(0.0,-1.0)).r;
    sum -= texelFetchWrap(coord.xy).r * 4.0;
    return sum*C_INV;
}

float calc_ddu_old(vec2 coord){
    vec2 sum = vec2(0.0);
    sum += texelFetchWrap(coord.xy + vec2(1.0,0.0)).rg;
    sum += texelFetchWrap(coord.xy + vec2(-1.0,0.0)).rg;
    sum += texelFetchWrap(coord.xy + vec2(0.0,1.0)).rg;
    sum += texelFetchWrap(coord.xy + vec2(0.0,-1.0)).rg;
    sum -= texelFetchWrap(coord.xy).rg * 4.0;
    return (sum.r - sum.g)*C_INV;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame < 10 || (iMouse.z > 0.0 && iMouse.z < iResolution.x/2.0)){
        vec2 coord = fragCoord.xy/iChannelResolution[0].xy*iChannelResolution[1].xy;
        float grey = dot(texelFetch(iChannel1, ivec2(coord), 0), vec4(.3, .59, .11, 0.0));
        fragColor = vec4(grey, 0.0, 0.0, 1.0);
        return;
    }

    if(iMouse.z > iResolution.x/2.0 || (iResolution.x < 200.0 && iFrame - 10 - ((iFrame - 10)/120)*120 >= 60)){
        float du_old = texelFetchWrap(fragCoord).g;
        float u_old = texelFetchWrap(fragCoord).r;
        float ddu = calc_ddu(fragCoord);
        float du = du_old + ddu;
        float u = u_old+du;
        fragColor = vec4(u,du,0,1);
    } else {
        float u_curr = texelFetchWrap(fragCoord).r;
        float du_curr = texelFetchWrap(fragCoord).g;
        float u_old = u_curr - du_curr;
        float ddu_old = calc_ddu_old(fragCoord);
        float du_old = du_curr - ddu_old;
        fragColor = vec4(u_old, du_old, 0, 1);
    }
}

