float sdCircle(vec2 p, float r) {
 	return length(p) - r;
}

vec2 cMov(vec2 p, vec2 trans) {
 	return p - trans;   
}

float asFilled(float d) {
    return (1.0 - smoothstep(0.,.01, d));
}    

float sMin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float objects = 300.;

    float ds = 1000.0;

    for(float i=0.; i<objects; ++i) {
        float r1 = rand(vec2(i));
        float r2 = rand(vec2(i*objects));
        vec2 loc = cMov(uv, vec2( cos(iTime*r1)*r1*1.5, cos(iTime*r2)*r2)*0.8);
        float dc = sdCircle(loc,0.1);
        ds = sMin(ds,dc,.19);
     }

    float d = asFilled(ds) * 0.4;

    fragColor = vec4(min(vec3(.0),vec3(.6,0.1,0.1) * 4.0 * ds) + vec3(0.9,0.1,0.1) * d, 1.0) ;

}
