float hash(vec2 p)
{
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float noise(vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 baseColor = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    vec2 noiseCoord = uv * 10.0;
    float noiseValue = 0.5 + 0.5 * sin(iTime + 10.0 * noise(noiseCoord));

    vec3 finalColor = baseColor * noiseValue;

    finalColor = mix(finalColor, vec3(1.0, 0.2, 0.7), 0.5 * sin(iTime));
    finalColor = mix(finalColor, vec3(0.2, 0.8, 0.5), 0.5 * cos(iTime));

    fragColor = vec4(finalColor, 1.0);
}
