void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.x, 
         F = U; F.x = fract(U.x) - .5;

    float s = mod(u.x,8.) - 4.;

    O *= 0.;
    if ( U.y > .3*sin(iTime) )
        O = s < 0. ? vec4(0,0,1,0) : vec4(1,1,0,0);                 // strips

    if ( sign(s) != sign(U.x) )                                     // disks
        O = mix(   O 
                 , mix( vec4(0,1,1,0), vec4(1,0,0,0), F.x+.5 )
                 , smoothstep( 3./R.x, 0., length(F) - .3 ) 
               );
}
