float random (in vec2 st) {
    return sin(sin(iTime + dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;

    vec4 texColor = texture(iChannel0, uv);

    float dispersionAmount = 0.5;
    float offset = 0.01;

    float radius = 0.5;

    float powerShakeX = 0.01;
    float powerShakeY = 0.01;

    float timeFunc = abs(sin(iTime) * noise(uv * sin(iTime) * 0.1));

    int timePeriodSec = 4;
    if (int(iTime) % timePeriodSec <= 2) {
        float powerHit = 0.002;
        timeFunc = abs(cos(iTime * noise(uv) * powerHit));
    }

    uv.x += clamp(uv.x, 0.0, radius) * timeFunc * powerShakeX;
    uv.y += clamp(uv.y, 0.0, radius) * timeFunc * powerShakeY;

    vec2 redUV = uv + vec2(offset * 1.0, 0.0) * sin(iTime);
    vec2 greenUV = uv + vec2(0.0, offset * 0.5);
    vec2 blueUV = uv + vec2(offset * -1.0, 0.0);

    float red = texture(iChannel0, redUV).r;
    float green = texture(iChannel0, greenUV).g;
    float blue = texture(iChannel0, blueUV).b;
    vec3 color = vec3(red, green, blue);


    fragColor = vec4(color, texColor.a);
}
