void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
    vec4 noise = texture(iChannel1, uv + iTime/10.);

    float maxr = iResolution.x / 10.;
    float d = distance(fragCoord.xy, iMouse.xy);
    d = d * (0.3 + noise.r);
    if (d <= maxr){
        float l = (0.3 * fragColor.r + 0.6 * fragColor.g + 0.1 * fragColor.b) * 1.3;
        float x = cos(3.1415927 * d / 2. / maxr);
        fragColor = vec4(x * l + (1. - x) * fragColor.r, (1. - x) * fragColor.g,
        (1. - x) * fragColor.b, 1.);
    }
}
