void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    int samp = int(iTime * iSampleRate) + skip * int(iSampleRate);
    int durationS = duration * int(iSampleRate);

    int a = int(uv.x * 35.0);

    float sampM = float(fracMod(samp, durationS, (449 - a))) / iSampleRate;

    float freq = pow(2.0, float(notes[a] - 69) / 12.0) * 440.0;

    float b = sin((uv.y - 0.5) * freq * 0.05 + sampM * 16.0) * 0.01 * exp(-5.0 * sampM);
    float c = (mod(uv.x * 35.0, 1.0) - 0.5) / 35.0;
    float d = min(abs(b - c) * 450.0, 1.0);

    vec3 col = mix(vec3(0.5, 0.9, 0.8), vec3(0.0), d);

    fragColor = vec4(col,1.0);
}

// 0 for sine, 1 for square, 2 for triangle, 3 for sawtooth
#define WAVEFORM 2

int duration = 7 * 60 + 30;
int skip = 0;

int notes[35] = int[](
    112, 110, 107, 105, 103, 102,
    100,  98,  95,  93,  91,  90,
     88,  86,  83,  81,  79,  78,
     76,  74,  71,  69,  67,  66,
     64,  62,  59,       55,     
     52,  50,  47,       43,       
     40,       35,
     28
);

int fracMod(int a, int num, int den) {
//  long a = ((long(samp) * (449 - i)) % durationS) / (449 - i); // why cant glsl just have longs :(
    int a1 = (((a % num) >> 2) * den) % (num >> 2);
    int a2 = ((a % num) & 0x3) * den;
    return (((a1 << 2) + a2) % num) / den; // 32-bit hack

}

float wave(float a) {
#if WAVEFORM == 0
    return sin(6.283185307 * a);
#elif WAVEFORM == 1
    return mod(a, 1.0) < 0.5 ? 1.0 : -1.0;
#elif WAVEFORM == 2
    return abs(mod(4.0 * a - 1.0, 4.0) - 2.0) - 1.0;
#elif WAVEFORM == 3
    return 1.0 - mod(a * 2.0, 2.0);
#endif
}

vec2 mainSound(int samp, float time)
{
    float sound;

    samp += skip * int(iSampleRate);

    int durationS = duration * int(iSampleRate);

    for (int i = 0; i < 35; i++) {
        float a = float(fracMod(samp, durationS, (449 - i))) / iSampleRate;

        float freq = pow(2.0, float(notes[i] - 69) / 12.0) * 440.0;

        sound += wave(a * freq) * exp(-5.0 * a);
    }
    sound /= 35.0;

    return vec2(sound);
}
