void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.y / iResolution.x;
    uv.y *= ratio;

    uv *= 20.0;
    vec2 grid = floor(uv - 1.0) + fract(uv) * (sin(iTime) + 1.0);
    uv = fract(uv);

    uv -= vec2(0.5, 0.5 * ratio);

    float rate1 = ((iTime * 0.5 + grid.x + grid.y) + grid.x + grid.y);
    float rate2 = iTime + sin(grid.x * 2.0) + sin(grid.y);
    float mixF = sin(iTime * 0.1);
    float rate = max(mixF, 0.0) * rate1 + max(-mixF, 0.0) * rate2;
    vec2 center = vec2(sin(rate * 2.0), sin(rate)) * vec2(0.41, 0.19);

    float len = length(center - uv);
    fragColor = vec4(0.5 + 0.5 * cos(rate + uv.xyx + vec3(0, grid.x, grid.y)), 1.0) * smoothstep(0.1, 0.095, len);
}
