void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Will make the dithering pattern more visible
    float noiseSpread = 0.1;

    //Amount of color values per color channel
    float amountOfColors = 5.0;




    vec2 uv = fragCoord/iResolution.xy;


    vec2 thresholdMapCoordinates = vec2(mod(fragCoord.x, 8.0), mod(fragCoord.y, 8.0));

    float noiseValue = texture(iChannel1, thresholdMapCoordinates).r - 0.5;   

    vec4 noiseAmount = vec4(noiseValue, noiseValue, noiseValue, 1.0);


    vec4 color = texture(iChannel0, uv) + noiseSpread * noiseAmount;

    color = (floor(color * (amountOfColors - 1.0) + 0.5)) / (amountOfColors - 1.0);


    fragColor = color;
}
