float time(float freq, float L) {
    return cos(iTime / freq) / (L+ 0.25);
}
float dir(vec2 uv, float freq, float dir, float L) {
    return fract(length(vec2(uv.x,uv.y) + dir * time(freq,L)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.y, iResolution.x);

    float L = length(uv);
    //orthogonal 
    float a = dir(vec2(uv.x,uv.x),1.0, 1.0, L);
    float b = dir(vec2(uv.y,uv.y),9.0, 1.0, L);
    float c = dir(vec2(uv.y,uv.y),7.0, -1.0, L);
    float d = dir(vec2(uv.x,uv.x),8.0, -1.0, L);
    //diagonal
    float w = dir(vec2(uv.x,uv.y),5.0, 1.0, L);
    float x = dir(vec2(-uv.x,uv.y),2.0, 1.0, L);
    float y = dir(vec2(uv.x,-uv.y),3.0, 1.0, L);
    float z = dir(vec2(-uv.x,-uv.y),4.0, 1.0, L);
    //center
    float o = fract(length(vec2(uv.x,uv.y) )+ time(6.0,L));

    vec3 C1 = vec3(230,25,75) / 255.0 * a;
    vec3 C2 = vec3(60,180,75) / 355.0 * b;
    vec3 C3 = vec3(255,225,25) / 255.0 * c;
    vec3 C4 = vec3(0,130,200) / 455.0 * d;
    vec3 C5 = vec3(245,130,48) / 255.0 * w;
    vec3 C9 = vec3(210,245,60) / 355.0 * x;
    vec3 C6 = vec3(145,30,180) / 155.0 * y;
    vec3 C7 = vec3(70,240,240) / 255.0 * z;
    vec3 C8 = vec3(240,50,230) / 455.0 * o;

    //vec3 col = (C1+C4+C5+C6) / 2.0;
    //vec3 col = (C2+C2+C3+C7) / 2.5;
    //vec3 col = (C7+C8+C9) / 2.;
    //vec3 col = (C5+C9+C6+C7)/2.;
    vec3 col = (C1+C2+C2+C3+C4+C5+C6+C7+C8+C9) / 5.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
