/* Original in 147 characters using @Xor's smooth AA technique: */
void mainImage(out vec4 c, in vec2 v) {
    v.x += sin(ceil(v /= 60.0)).y * iTime;
    c = 2.0 * fract(0.64 * ceil(vec4(v, v + 1.0)))
            * min(9.0 - length(0.5 - fract(v)) * 22.0, 1.0);
}
