void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //vec2 texCoords = uv + vec2(0.0) + vec2(iTime * 0.12, 0.34);

    //texCoords += texture(iChannel1, uv).xy * 0.03 ;


    vec2 texCoords1 = uv + vec2(iTime * 0.12, 0.34);

    texCoords1 += texture(iChannel1, texCoords1).xy;

    vec2 texCoords2 = uv - vec2(iTime * 0.13, 0.26);

    texCoords2 += texture(iChannel2, texCoords2).yx;

    vec2 texCoords = uv + (texCoords1 + texCoords2) * 0.01;


    // Output to screen
    fragColor = texture(iChannel0, texCoords);
}
