#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
void mainImage(out vec4 o, vec2 u) {
    vec2 v, q, r = iResolution.xy;
    u = (u - r / 2.) / r.y;

    o = vec4(1, 2, 3, 0);

    float i, t = iTime;

    while(t++, i++ < 18.)

        v = cos(t * vec2(3, 2) + vec2(0, 11)) - 6. * u, 

        u *= rot(i - .1 * t),

        u += cos(4. / exp(dot(o, o) / 1e2) + t) / 5.,

        q = sin(
                u / (2. - dot(u, u)) 
                - 9. * u.yx 
                + t
            ), 

        q *= 1. + i * dot(v, v),

        o += (cos(vec4(1, 8, 4, 0) + t) + 1.) / length(q); 


    o *= .15;
    o -= dot(u, u) / 30.;
}
