#endif

float time;
#define FAR 150.
#define ID_NONE -1.
#define ID_TABLE 0.
#define ID_VASE 1.
#define ID_GLASS 2.
#define ID_BALL 10.
#define ID_GLASS_HOLDER 11.

float random( float n ) {
    return fract(sin(n)*43758.5453);
}

vec3 sunDir = normalize (vec3 (1., 1., -1.));

float map (vec3 p, inout Object object) {
	vec3 q;
	float d;

	object = Object(FAR, ID_NONE, p);

	//Стол
	Object OBJ0 = Object(FAR, ID_TABLE, p);
	{
		q = p;
		q.y -= -7.5;
		d = AND(TF_BALL(q.xz, 25.), TF_BETWEEN(q.y, 0.3));
		OBJ0.distance = d;
		OBJ0.position = q;
	}
	object = OR(object, OBJ0);

	//Ваза
	Object OBJ1 = Object(FAR, ID_VASE, p);
	{
		q = p;
		d = TF_BOX_ROUND(q, 5.+max(q.y*0.5, -1.), 1. + q.y*0.2);
		d = abs(d) - 0.5;
		d = AND(d, TF_BEFORE(q.y, 4.));
		OBJ1.distance = d;
		OBJ1.position = q;
	}
	object = OR(object, OBJ1);

	//Шары
	Object OBJ2 = Object(FAR, ID_BALL, p);
	{
		q = p;
		q.x = abs(q.x) -2.;
		d = TF_BALL(q, 2.);
		OBJ2.distance = d;
		OBJ2.position = q;
	}
	object = OR(object, OBJ2);

	vec3 qGlass;
	//Стаканы
	Object OBJ3 = Object(FAR, ID_GLASS, p);
	{
		q = p;
		float id = TF_REPLICA_ANGLE(q.xz, 4., 0.);
		q.z -= 18.;
		qGlass = q;

		float R = 3.+ (q.y+4.)*(q.y+4.)*0.02;
		d = AND(TF_BALL(q.xz, R), TF_AFTER(q.y, -6.));
		d = abs(d) - 0.25;

		//Капли
		vec3 qq = q;
		qq.y -= -time*0.8;
		float id3 = TF_REPLICA(qq.y, 1.5);
		float rnd = random(id3);
		float id2 = TF_REPLICA_ANGLE(qq.xz, 6., rnd*2.);
		qq.z -= R;
		float d1 = AND(TF_BALL(qq, 0.7*rnd), TF_AFTER(q.y, -6.));
		d = OR(d, d1);
		d = AND(d, TF_BEFORE(q.y, 6.));
		OBJ3.distance = d;
		OBJ3.position = q;
		OBJ3.id = ID_GLASS + id;
	}
	object = OR(object, OBJ3);

	//Подстаканники
	Object OBJ4 = Object(FAR, ID_GLASS_HOLDER, p);
	{
		q = qGlass;
		q.y -= -6.4;
		d = AND(TF_BALL(q.xz, 6.), TF_BETWEEN(q.y, 0.1));
		OBJ4.distance = d;
		OBJ4.position = q;
	}
	object = OR(object, OBJ4);

	return object.distance;
}


float map ( in vec3 p ) {
	Object object;
	return map (p, object);
}

vec3 mapNormal (vec3 p, float eps) {
	vec2 e = vec2 (eps, -eps);
	vec4 v = vec4 (
		map (p + e.xxx), 
		map (p + e.xyy),
	 	map (p + e.yxy), 
		map (p + e.yyx)
	);
	return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * vec3 (v.y, v.z, v.w));
}


float rayMarch(inout Ray ray) {
	ray.distance = ray.near;
	float steps;
	for (float i = 0.; i < 200.; ++i) {
		ray.position = ray.origin + ray.direction * ray.distance;
		ray.object.distance = map(ray.position, ray.object);
		ray.hit = abs(ray.object.distance) < ray.epsilon;
		if (ray.hit) break;
		ray.distance += ray.object.distance*ray.swing;
		if (ray.distance>ray.far) break;
		steps = i + 1.;
		if (steps>ray.steps) break;
	}
	return steps;
}

struct Material {
	vec3 color;
	float spec;
	bool transp;
};

vec3 lighting(Ray ray, vec3 sunDir, Material mat) {
	float diff = max (dot (sunDir, ray.normal), 0.);
	float spec = pow (max (dot (ray.direction, reflect (sunDir, ray.normal)), 0.), 32.);
	return mat.color * (0.2 + 0.8 * diff) + mat.spec * spec;
}

Material getMaterial (Ray ray) {
	vec3 p = ray.position;
	vec3 q = ray.object.position;
	vec3 n = ray.normal;
	Material mat = Material(vec3(1), 1., false);
	if (ray.object.id==ID_TABLE) {
		mat = Material(vec3(0, 0, 0.6), 0.5, true);
	} else if (ray.object.id==ID_VASE) {
		mat = Material(vec3( 0, 0, 1), 3., true);
	} else if (ray.object.id==ID_GLASS + 0.) {
		mat = Material(vec3( 0, 0.7, 0), 3., true);
	} else if (ray.object.id==ID_GLASS + 1.) {
		mat = Material(vec3(0.7,  0, 0), 3., true);
	} else if (ray.object.id==ID_GLASS + 2.) {
		mat = Material(vec3(0.7,  0.7, 0), 3., true);
	} else if (ray.object.id==ID_GLASS + 3.) {
		mat = Material(vec3(0, 0.7,  0.7), 3., true);
	} else if (ray.object.id==ID_BALL) {
		mat = Material(vec3(1,0,0), 1., false);
		if (p.x>0.) mat = Material(vec3(0,1,0), 2., false);
	} else if (ray.object.id==ID_GLASS_HOLDER) {
		mat = Material(vec3(1), 0.2, false);
		float d = TF_BALL(q, 5.);
		d = abs(d)-0.1;
		if (d<0.) mat.color = vec3(0,0.8,0);
	}
	return mat;
}

vec3 bgCol (Ray ray) {
	return textureCube(u_textureCube0, ray.direction).rgb;
}

vec3 getColorReflect(Ray ray) {
	ray.origin = ray.position;
	ray.direction = reflect(ray.direction, ray.normal);
	return bgCol (ray);
}

vec3 getColorRefract(inout Ray ray, float eta) {
	//Формирование луча с поверхности элемента вглубь элемента
	ray.origin 		= ray.position;
	ray.direction 	= refract(ray.direction, ray.normal*ray.swing, eta);
	ray.swing 		= -ray.swing;
	//Перемещение луча до обратной стороны элемента
	rayMarch(ray);
	//Нормаль в точке обратной стороны элемента
	ray.normal = mapNormal(ray.position, 0.001);
	//Подготовка луча к следующему перемещению
	ray.origin 		= ray.position;
	ray.direction 	= refract(ray.direction, ray.normal*ray.swing, 1./eta);
	ray.swing 		= -ray.swing;
	//Перемещение луча до следующего элемента
	rayMarch(ray);
	//Цвет элемента после преломления
	vec3 colR = vec3(0);
	if (ray.distance<ray.far) {
		ray.normal = mapNormal(ray.position, 0.001);
		Material mat = getMaterial(ray);
		colR = lighting(ray, sunDir, mat);
	} else {
		colR = bgCol(ray);
	}
	return colR;
}

vec3 render (Ray ray) { 
	vec3 col = vec3(0);

	float refracts = 0.;
	const float refractsMax = 3.;//6

	for(float i=0.; i<refractsMax; i++) {
		refracts++;

		rayMarch(ray);

		if (ray.distance<ray.far) {
			ray.normal = mapNormal(ray.position, 0.001);
			Material mat = getMaterial(ray);
			col += lighting(ray, sunDir, mat);
			if (mat.transp) {
				float fres = 1.3 - dot(-ray.direction, ray.normal);
				vec3 colRefl = getColorReflect(ray);
				vec3 colRefr = getColorRefract(ray, 0.95);
				col += mix(colRefr, colRefl, fres);
			}
		} else {
			col += bgCol (ray);
		}

		if (ray.distance>=ray.far) break;
	}

	return col/refracts;
}

void main() {
	time = u_time;

	float aspect = u_canvas.x/u_canvas.y;
	vec2 uv = gl_FragCoord.xy/u_canvas.xy;
	uv = uv*2. - 1.;

	vec2 mouse = u_mouse.xy / u_canvas.xy - 0.5;
	if (u_mouse.xy==vec2(0)) mouse = vec2(0);

	vec2 ori = vec2(
		u_mouse.z==0. ? 0.5*sin(time*0.2) : mouse.y*PI*2.,
		u_mouse.z==0. ? time*0.2 : mouse.x*PI*2.
	);

	#if 1
		ori.x = clamp(ori.x, -PI/2., PI/2.);
	#else
		ori.x = clamp(ori.x, -PI/2., 0.);
	#endif

	Camera cam;
	{
		cam.fov     = 45.;
		cam.aspect  = aspect;
		cam.origin  = vec3 (0., 0., -30);
		cam.target  = vec3(0);
		cam.up 		= vec3(0,1,0);
		cam.vMat 	= mat3(1);//TF_ROTATE_Y(ori.y) * TF_ROTATE_X(ori.x);
		cam.mMat	= TF_ROTATE_Y(ori.y) * TF_ROTATE_X(ori.x);
	}

	Ray ray = lookAt(uv, cam);
	{
		ray.near 	= 0.01;
		ray.far  	= FAR;
		ray.epsilon = 0.001;
		ray.swing	= 1.;
		ray.steps 	= 150.;
	}

	vec3 ro = ray.origin;
	vec3 rd = ray.direction;

	#if 1
		sunDir = cam.mMat * sunDir;
	#else
		sunDir = -ray.direction;
	#endif

	vec3 col = render (ray);
	gl_FragColor = vec4 (col, 1.);
}

const float PI = radians(180.);
//Камера
struct Camera {
	//Задаваемые параметры
	float fov, aspect;
	vec3  origin, target, up;
	mat3 vMat, mMat;
	//Расчетные параметры
	float factor;
	vec3  forward, right, position, coord;
	mat3 pMat, uMat;
};

//Параметры объекта
struct Object {
	float   distance;	//Последнее приближение к объекту сцены
	float 	id;			//id найденого объека сцены
	vec3 	position;	//Координатв поверхности объекта
};

//Луч
struct Ray {
	//Задаваемые параметры
	vec3  origin;		//Начало луча
	vec3  direction;	//Направление луча
	float near;			//Минимальное расстояние от камеры (начало сцены)
	float far;			//Максивальное расстояние от камеры (конец сцены)
	float epsilon;		//Точность обнаружения поверхности
	float steps;		//Максимальное число итераций
	float swing;		//"+" -колебание в найденной точке, "-"
	//Вычисляемые параметры
	float distance; 	//Расстояние до объекта сцены от ray.origin в направлении ray.direction
	vec3  position; 	//Точка поверхности
	vec3  normal;		//Нормаль в точке поверхности
	bool  hit;			//Флаг нахождения точки поверхности с заданной точностью
	Object object;		//Параметры объекта (можно формировать непосредственно в карте расстояний)
};
//Формирование луча камеры
Ray lookAt (in vec2 uv, inout Camera cam) {
	//Расчетные характеристики камеры
	cam.factor		= 1.0/tan(radians(cam.fov/2.));
	cam.forward 	= normalize(cam.target-cam.origin); 
	cam.right 		= normalize(cross(cam.up, cam.forward));
	cam.up 			= cross(cam.forward, cam.right);
	cam.position 	= cam.origin + cam.factor * cam.forward;
	cam.coord 		= cam.position + uv.x * cam.right*cam.aspect + uv.y * cam.up;
	cam.pMat 		= mat3(cam.right, cam.up, cam.forward);

	//Формирование луча
	Ray ray;
	{
		ray.origin 		= cam.mMat * cam.origin;
		ray.direction 	= cam.mMat * normalize( cam.pMat * cam.vMat * vec3(uv.x*cam.aspect, uv.y, cam.factor));
		//Умолчания
		ray.near		= 0.01;
		ray.far			= 100.;
		ray.epsilon		= 0.001;
		ray.swing		= 1.0;
		ray.steps		= 200.;
	}
	return ray;
}
//-------------------функции трансформации и позиционирования объектов сцены
//Допустимая ошибка (для удаления артефактов) 1./min(u_canvas.x, u_canvas.y)
float EPS = 0.01;
//Функция расстояния до фигуры со скругленными ребрами (объединение и сглаживание с увеличением на R)
//float d - сглаживание фигуры
//vec2 d - объединение и сглаживание двух фигур
//vec3 d - объединение и сглаживание трех фигур
//vec4 d - объединение и сглаживание четырех фигур
#define TF_ROUND(d, R) (length(max(d, 0.)) - (R))
//Функция расстояния до бокса (p vec3) или прямоугольника (p vec2) со скругленными ребрами
//Функция расстояния до капсулы, если два размера 0
#define TF_BOX_ROUND(p, S, R) TF_ROUND(abs(p)-(S), R)
#define TF_BOX_ROUND1(p, S, R) TF_ROUND(abs(p)-(S) + R, R)
//Функция расстояния до бокса (p vec3) или прямоугольника (p vec2)
#define TF_BOX(p, S) TF_BOX_ROUND(p, S, 0.)
#define TF_BOX1(p, S) TF_BOX_ROUND(p, S, EPS)
#define TF_BOXE(p, S) TF_BOX_ROUND(p, S - EPS, EPS)
#define TF_BOX3D(p, S) max(abs((p).x)-vec3(S).x, max(abs((p).y)-vec3(S).y, abs((p).z)-vec3(S).z))
#define TF_BOX2D(p, S) max(abs((p).x)-vec2(S).x, abs((p).y)-vec2(S).y)
#define TF_BOX2D_SD(p, S) TF_BOX(p, S) + min(TF_BOX2D(p, S), 0.)
#define TF_BOX3D_SD(p, S) TF_BOX(p, S) + min(TF_BOX3D(p, S), 0.)
//Функция расстояния до шара (p vec3) или круга (p vec2)
#define TF_BALL(p, R) TF_ROUND(abs(p), R)
//Эллипс
#define TF_ELLIPSE3D(p, r) min ((r).x, min((r).y, (r).z)) * TF_BALL((p)/(r), 1.)
#define TF_ELLIPSE2D(p, r) min ((r).x, (r).y) * TF_BALL((p)/(r), 1.)
//Отрезок от a до b радиусом r
#define TF_SEGMENT(p, a, b, r) TF_BALL((p)-(a) - ((b)-(a)) * clamp( dot((p)-(a),(b)-(a))/dot((b)-(a),(b)-(a)), 0.0, 1.0 ), r)
//Функция расстояния до пространства ДО p1
#define TF_BEFORE(p, p1) (p - (p1))
#define TF_BEFORE1(p, p1) TF_ROUND(p - (p1) + EPS, EPS)
//Функция расстояния до пространства ДО normal с отступом p1
#define TF_BEFORE_PLANE(p, normal, p1) dot(p, normal) - p1
//Функция расстояния до пространства ПОСЛЕ p1
#define TF_AFTER(p, p1) (-p + (p1))
#define TF_AFTER1(p, p1) TF_ROUND(-p + (p1) + EPS, EPS)
//Функция расстояния до пространства ПОСЛЕ normal с отступом p1
#define TF_AFTER_PLANE(p, normal, p1) dot(p, normal) + p1
//Функция расстояния до симметричного пространства МЕЖДУ -p1 и +p1
//#define TF_BETWEEN(p, p1) TF_ROUND(abs(p) - (p1) + EPS, EPS)
#define TF_BETWEEN(p, p1) (abs(p) - (p1))
//Функция расстояния до несимметричного пространства МЕЖДУ p1 и p2
//#define TF_BETWEEN2(p, p1, p2) TF_ROUND( abs(p - 0.5*(p1+p2)) - 0.5*(p2-p1) + EPS, EPS)
#define TF_BETWEEN2(p, p1, p2) (abs(p - 0.5*((p1)+(p2))) - 0.5*((p2)-(p1)))
//Перемещение пространства p (float p...vec4 p)
#define TF_TRANSLATE(p, d) p -= d;
//Масштаб
#define TF_SCALE(p, s) p /= s
//Преобразование двух координат пространства в круг
#define TF_CIRCLE(p, R) p = vec2(TF_BALL(p.xy, R), (R)*atan(p.x,p.y))

//Вращение пространства p (vec2 p)
#if 0
	#define TF_ROTATE(p, a) p = mat2(cos(a), sin(a), -sin(a), cos(a)) * p
#else
	#define TF_ROTATE(p, a) p = p.xy * cos (a) * vec2 (1., 1.) + p.yx * sin (a) * vec2 (-1., 1.)
#endif
#define TF_ROTATE_MAT2(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define TF_ROTATE_X(a) mat3(1.0,    0.0, 0.0,       0.0, cos(a), -sin(a),   0.0, sin(a), cos(a))
#define TF_ROTATE_Y(a) mat3(cos(a), 0.0, -sin(a),   0.0, 1.0, 0.0,   	    sin(a), 0.0, cos(a))
#define TF_ROTATE_Z(a) mat3(cos(a), -sin(a), 0.0,   sin(a), cos(a), 0.0,   	0.0, 0.0, 1.0)
#define TF_ROTATE_X_90(p) p.xyz = p.xzy
#define TF_ROTATE_Y_90(p) p.xyz = p.zyx
#define TF_ROTATE_Z_90(p) p.xyz = p.yxz
//Отражение пространства p (float p...vec4 p)
#define TF_MIRROR(p, d) p = abs(p) - (d)
//Разбиение пространства на повторяющиеся. Возвращает id центра ячейки
//float p - пространство 1D
//vec2 p - пространство 2D
//vec3 p - пространство 3D
#define TF_REPLICA(p, d) \
	floor((p)/(d) + 0.5);\
	p = mod((p) + 0.5*(d), d) - 0.5*(d)
#define TF_REPLICA1(p, d) \
	floor((p)/(d) + 0.5);\
	p = p - (d) * floor((p)/(d) + 0.5)
//Разбиение пространства на повторяющиеся в диапазоне id от ida до idb включительно. Возвращает id центра ячейки
#define TF_REPLICA_LIMIT(p, d, ida, idb) \
	floor((p)/(d) + 0.5);\
	p = p-(d)*clamp(floor((p)/(d) + 0.5), ida, idb)
#define TF_REPLICA_LIMIT_MIRROR(p, d, id) \
	floor((p=p-0.5*(d))/(d) + 0.5);\
	p = p-(d)*clamp(floor((p)/(d) + 0.5), -id, id-1.)

//Разбиение пространства на повторяющиеся сектора в плоскости vec2 p в полярной системе координат
//Возвращает id сектора	
#define TF_REPLICA_ANGLE_POLAR(p, n, off)\
	floor(mod(atan(p.x, p.y) + off, 2.*PI)/(2.*PI/(n)) + 0.5);\
	p = vec2(atan(p.x,p.y) + off, length(p.xy));\
	p.x = mod(p.x + 0.5*(2.*PI/(n)), (2.*PI/(n))) - 0.5*(2.*PI/(n))
//Разбиение пространства на повторяющиеся сектора в плоскости vec2 p в декартовой системе координат
//Возвращает id сектора	
#if 1
	#define TF_REPLICA_ANGLE(p, n, off) \
		floor(mod(atan(p.x, p.y) + PI /(n) + off, 2.*PI)/(2.* PI /(n)));\
		TF_ROTATE(p.xy, -off);\
		TF_ROTATE(p.xy, (2.* PI /(n)) * floor(atan(p.x,p.y)/(2.* PI /(n)) + 0.5))
#else
	#if 0
		#define TF_REPLICA_ANGLE(p, n, off) \
			floor(mod(atan(p.x, p.y) + off, 2.*PI)/(2.*PI/(n)) + 0.5);\
			p = vec2(atan(p.x,p.y) + off, length(p.xy));\
			p.x = mod(p.x + 0.5*(2.*PI/(n)), (2.*PI/(n))) - 0.5*(2.*PI/(n));\
			p = p.y * vec2(sin(p.x), cos(p.x))
	#else
		#define TF_REPLICA_ANGLE(p, n, off) \
			TF_REPLICA_ANGLE_POLAR(p, n, off);\
			p = p.y * vec2(sin(p.x), cos(p.x))
	#endif
#endif

//По p2.xy формирует координату винта вдоль p1 с шагом s 
//Возвращает ID
#define TF_REPLICA_SPIN(p1, p2, s) (\
	p1 += atan(p2.x,p2.y) * (s),\
	TF_REPLICA(p1, 2. * PI / 1. * (s))\
)

#define TF_REPLICA_SPIN_LIMIT(p1, p2, s, ida, idb) (\
	p1 += atan(p2.x,p2.y) * (s),\
	TF_REPLICA_LIMIT(p1, 2. * PI / 1. * (s), ida, idb)\
)	
//Формирование в плоскости p.xy n ветвей равномерной бесконечной спирали.
//Возвращает в p.x расстояние до спирали.
#if 1
	#define TF_SPIRAL(p, n)\
		p.x = atan(p.x,p.y)*(n)/(2.*PI) - length(p.xy) - 0.5;\
		p.x -= 1. * floor(p.x/1. + 0.5)
#else
	#define TF_SPIRAL(p, n)\
		p.xy = vec2(atan(p.x,p.y), length(p.xy));\
