//"Chromatic Derivatives": https://shadertoy.com/view/4c2fWc

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float RADIUS = 4.0;
    float CHROMATIC = uv.x<0.5? 0.0: 2.0;

    vec4 col = texture(iChannel0, uv);
    vec4 mip = texture(iChannel0, uv, log2(RADIUS));

    col.rgb += CHROMATIC * RADIUS * dFdx(mip.rgb)*vec3(-1,0,1);

    fragColor = col;
    fragColor.rgb *= min(abs(fragCoord.x-0.5*iResolution.x), 1.0);
}
