#define iM iMouse
#define iR iResolution
#define use_rgb_select 1

#define individual 1

void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/iR.xy;

    vec3 tex = texture(iChannel0, uv).rgb;
    vec3 col = tex;

    // less expensive but u cant color select (which is the purpose of this effect)
    // col *= vec3(step(uv.y, 1.) / 2., step(uv.y, .66), step(uv.y, 0.33)*2.) / 2.;
    // if u wanna use delete everything below (besides the output obv)

    float r = tex.r;
    float g = col.g;
    float b = col.b;

    col *= vec3(r+g+b)/3.; // grayscale

    float val = (iM.z > 0.) ? iM.y/iR.y : .5;

    float value = round(mix(0.,3.,val));

    vec3 rgb = vec3(0.,0.,0.);

    if (individual == 1) {
        rgb.r = (val < 1.0000 && val > 0.6666) ? 1. : 0.;
        rgb.g = (val < 0.6666 && val > 0.3333) ? 1. : 0.;
        rgb.b = (val < 0.3333 && val > 0.0000) ? 1. : 0.;
    } else {
        rgb.r = clamp(value,2.,3.)-2.;
        rgb.g = clamp(value,1.,2.)-1.;
        rgb.b = clamp(value,0.,1.)-0.;
    }

    col = (uv.y < 1.0000 && uv.y > 0.6666) ? vec3(r,0.,0.) * rgb.r : col; // R
    col = (uv.y < 0.6666 && uv.y > 0.3333) ? vec3(0.,g,0.) * rgb.g : col; // G
    col = (uv.y < 0.3333 && uv.y > 0.0000) ? vec3(0.,0.,b) * rgb.b : col; // B

    col = mix(tex, pow(col, vec3(1.5)), vec3(use_rgb_select));

    O = vec4(col,1.0);
}
