void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 guv = uv - vec2(.5,.5);
    float angle = atan(guv.x,guv.y);
    float normalizedAngle = (angle + 3.14159265358979323846) / (2.0 * 3.14159265358979323846);

    float d = length(uv-vec2(.5,.5));
    float wave1 = cos((normalizedAngle*44.)+iTime*10.)*.02;
    float wave2 = cos((normalizedAngle*88.)+iTime)*.01;

    float v = smoothstep(.2,.201,d+wave1+wave2);


    fragColor = vec4(v,v,v,1.0);
}
