float PI = 3.14159265;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.*(2.*fragCoord - iResolution.xy)/iResolution.y;

    vec2 ballPos = vec2(
        (cos(mod(-iTime,PI*2.)/2.))*4.*iResolution.x/iResolution.y,
        abs(sin(iTime*2.))*2.-1.
    );
    float cir = smoothstep(1.01,1.,
        length(
            uv-ballPos
        )
    );
    float fl = smoothstep(-2.,-2.01,uv.y);
    float c = max(cir,fl);

    fragColor = vec4(vec3(c),1.0);
}
