void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Define the resolution
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Create a gradient from green to blue
    vec3 gradientColor = mix(vec3(0.0, 1.0, 0.3), vec3(0.0, 0.55, 1.4), uv.y + uv.x - 0.5);

    float squareSize = 0.08; 
    float squareSpacing = 0.12;
    float squareX = mod(uv.x, squareSpacing);
    float squareY = mod(uv.y, squareSpacing);
    vec3 squareColor = vec3(step(squareSize, squareX) * step(squareSize, squareY));

    // Combine gradient and squares
    fragColor = vec4(gradientColor + squareColor, 1.0);
}
